mapper.insert:
/* insertCalendar */
INSERT INTO calendar (
    title
    , is_important
    , location
    , is_lunar
    , start_at
    , end_at
    , is_all_day
    , is_repeat
    , repeat_type
    , is_private
    , color
    , description
    , user_id
    , created_at
)
VALUES (
    #{title}
    , #{isImportant}
    , #{location}
    , #{isLunar}
    , #{startAt}
    , #{endAt}
    , #{isAllDay}
    , #{isRepeat}
    , #{repeatType}
    , #{isPrivate}
    , #{color}
    , #{description}
    , #{userId}
    , NOW()
)

mapper.select:
/* findCalendarOne */
SELECT
    *
FROM calendar
WHERE
    id = #{value}

mapper.update:
/* updateCalendar */
UPDATE calendar SET
    title = #{title}
    , start_at = #{startAt}
    , end_at = #{endAt}
    , updated_at = NOW()

        , is_important = #{isImportant}


        , location = #{location}


        , is_lunar = #{isLunar}


        , is_all_day = #{isAllDay}


        , is_repeat = #{isRepeat}


        , repeat_type = #{repeatType}


        , is_private = #{isPrivate}


        , color = #{color}


        , description = #{description}

WHERE
    id = #{id}

mapper.select:
/* findCalendarByDay */
SELECT
    *
FROM calendar
WHERE
    user_id = #{userId}
    AND DATE_FORMAT(start_at, '%Y-%m-%d') = #{date}
