mapper.insert:
/* insertCalendar */
INSERT INTO calendar (
    title
    , is_important
    , location
    , is_lunar
    , start_at
    , end_at
    , is_all_day
    , is_repeat
    , repeat_type
    , is_private
    , color
    , description
    , user_id
    , created_at
)
VALUES (
    #{title}
    , #{isImportant}
    , #{location}
    , #{isLunar}
    , #{startAt}
    , #{endAt}
    , #{isAllDay}
    , #{isRepeat}
    , #{repeatType}
    , #{isPrivate}
    , #{color}
    , #{description}
    , #{userId}
    , NOW()
)
